import * as React from "react"
import {useForm} from "react-hook-form";

type formType = {
    username: string
    password: string
}

const Register = () => {
    
    const {register, handleSubmit, errors} = useForm<formType>();

    const onSubmit = handleSubmit((data: formType) => {
        console.log(data.username)
        console.log(data.password)
    })

    return (
        <div>
            Register Page
            <form onSubmit={onSubmit}>
                <input type="text" ref={register({required:true})} name="username" placeholder="Username" />
                <input type="password" ref={register({required:"PASSWORD IS REQUIRED", minLength:8})} name="password" placeholder="Password" />
                {errors.password}
                <button type='submit'>Submit </button>
                {errors.password && <p>Password is not valid</p>}
            </form> 
        </div>
    )
}

export default Register;
