// @ts-nocheck

import { toErrorMap } from './../utils/toErrorMap';
import * as React from "react"
import {useForm} from "react-hook-form";
import { useRegisterMutation } from "../generated/graphql";

type formType = {
    username: string
    password: string
}

const Register: React.FC = () => {
    
    const {register, handleSubmit, errors} = useForm<formType>();

    const [,registerUser] = useRegisterMutation();
    const [submissionErrs, setSubmissionErrs] = useState({});
    const [numSubmissionErrs, setNumSubmissionErrs] = useState(0);

    const onSubmit = handleSubmit(async (data) => {
       const response = await registerUser(data);
       if (response.data?.register.errors) {
            const errorMap = toErrorMap(response.data.register.errors);
            setSubmissionErrs(errorMap);
            setNumSubmissionErrs(errorMap.keys().length);
       }
    })

    return (
        <div>
            Register Page
            <form onSubmit={onSubmit}>
                <input type="text" ref={register({required:true})} name="username" placeholder="Username" />
                <input type="password" ref={register({required:"Password required", minLength:{value:8, message:"Password is too short"}})} name="password" placeholder="Password" />
                <button type='submit'>Submit </button>
                {errors.password && errors.password.message}
            </form> 
            {(numSubmissionErrs !== 0) ? submissionErrs.keys().forEach(errKey => submissionErrs[errKey]) : ""}
        </div>
    )
}

export default Register;
