import * as React from "react"
import {useForm} from "react-hook-form";

type formType = {
    username: string
    password: string
}

const REGISTER_MUT = `
mutation Register($username: String!, $password: String!) {
  register(options:{username:$username,password:$password}) {
    errors {
      field,
      message
    }
    user {
      id
      username
    }
  }
}

`

const Register: React.FC = () => {
    
    const {register, handleSubmit, errors} = useForm<formType>();

    const onSubmit = handleSubmit((data: formType) => {
        console.log(data.username)
        console.log(data.password)
    })

    return (
        <div>
            Register Page
            <form onSubmit={onSubmit}>
                <input type="text" ref={register({required:true})} name="username" placeholder="Username" />
                <input type="password" ref={register({required:"Password required", minLength:{value:8, message:"Password is too short"}})} name="password" placeholder="Password" />
                <button type='submit'>Submit </button>
                {errors.password && errors.password.message}
            </form> 
        </div>
    )
}

export default Register;
